import React, { useRef, useEffect, useCallback } from 'react'
import { useSpring, animated } from 'react-spring'
import styled from 'styled-components'
import { MdClose } from 'react-icons/md'
import { Link } from 'react-router-dom'
import { useState } from 'react'

const Background = styled.div`
  width: 100%;
  height: 100%;
  background: rgba(0, 0, 0, 0.8);
  position: fixed;
  display: flex;
  justify-content: center;
  align-items: center;
`

const ModalWrapper = styled.div`
  width: 800px;
  height: 500px;
  box-shadow: 0 5px 16px rgba(0, 0, 0, 0.2);
  background: #fff;
  color: #000;
  display: grid;
  grid-template-columns: 1fr 1fr;
  position: relative;
  z-index: 10;
  border-radius: 10px;
`

const ModalImg = styled.img`
  width: 100%;
  height: 100%;
  border-radius: 10px 0 0 10px;
  background: #000;
`

const ModalContentLeft = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: flexstart;
  align-items: center;
  line-height: 1.8;
  color: #141414;
  p {
    margin-bottom: 1rem;
  }
  button {
    padding: 10px 24px;
    background: #141414;
    color: #fff;
    border: none;
  }
`
const ModalContentRight = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  line-height: 1.8;
  color: #141414;
  p {
    margin-bottom: 1rem;
  }
  button {
    padding: 10px 24px;
    background: #141414;
    color: #fff;
    border: none;
  }
`

const CloseModalButton = styled(MdClose)`
  cursor: pointer;
  position: absolute;
  top: 20px;
  right: 20px;
  width: 32px;
  height: 32px;
  padding: 0;
  z-index: 10;
`

export const Modal = ({
  showModal,
  setShowModal,
  score,
  setScore,
  setAccuracyList,
  accuracyList,
  accuracyAvg,
  setSyncList,
  syncList,
  syncAvg,
}) => {
  const modalRef = useRef()

  const animation = useSpring({
    config: {
      duration: 250,
    },
    opacity: showModal ? 1 : 0,
    transform: showModal ? `translateY(0%)` : `translateY(-100%)`,
  })

  const closeModal = (e) => {
    if (modalRef.current === e.target) {
      setShowModal(false)

      // setFlag(true)

      if (score === 1) {
        increaseOneScore()
      } else if (score === 2) {
        increaseTwoScore()
      } else if (score === 3) {
        increaseThreeScore()
      } else if (score === 4) {
        increaseFourScore()
      } else if (score === 5) {
        increaseFiveScore()
      } else if (score === 6) {
        increaseSixScore()
      } else if (score === 7) {
        increaseSevenScore()
      } else if (score === 8) {
        increaseEightScore()
      } else if (score === 9) {
        increaseNineScore()
      } else if (score === 10) {
        increaseTenScore()
      }

      setAccuracyDatas((oldDatas) => [...oldDatas, Math.floor(accuracyAvg)])
      localStorage.setItem('accuracyDatas', JSON.stringify(accuracyDatas))

      setSyncDatas((oldDatas) => [...oldDatas, Math.floor(syncAvg)])
      localStorage.setItem('syncDatas', JSON.stringify(syncDatas))

      setTime((oldDatas) => [...oldDatas, new Date().toLocaleTimeString()])
      localStorage.setItem('time', JSON.stringify(time))
      // setAccuracyList([])
      // setSyncList([])
      setScore(0)
    }
  }

  const closeModalWithCross = () => {
    setShowModal(false)

    // setFlag(true)

    if (score === 1) {
      increaseOneScore()
    } else if (score === 2) {
      increaseTwoScore()
    } else if (score === 3) {
      increaseThreeScore()
    } else if (score === 4) {
      increaseFourScore()
    } else if (score === 5) {
      increaseFiveScore()
    } else if (score === 6) {
      increaseSixScore()
    } else if (score === 7) {
      increaseSevenScore()
    } else if (score === 8) {
      increaseEightScore()
    } else if (score === 9) {
      increaseNineScore()
    } else if (score === 10) {
      increaseTenScore()
    }

    setAccuracyDatas((oldDatas) => [...oldDatas, Math.floor(accuracyAvg)])
    localStorage.setItem('accuracyDatas', JSON.stringify(accuracyDatas))

    setSyncDatas((oldDatas) => [...oldDatas, Math.floor(syncAvg)])
    localStorage.setItem('syncDatas', JSON.stringify(syncDatas))

    setTime((oldDatas) => [...oldDatas, new Date().toLocaleTimeString()])
    localStorage.setItem('time', JSON.stringify(time))
    // setAccuracyList([])
    // setSyncList([])
    setScore(0)
  }

  // const [zeroScore, setZeroScore] = useState(localStorage.getItem('zeroScore'))
  const [oneScore, setOneScore] = useState(localStorage.getItem('oneScore'))
  const [twoScore, setTwoScore] = useState(localStorage.getItem('twoScore'))
  const [threeScore, setThreeScore] = useState(
    localStorage.getItem('threeScore')
  )
  const [fourScore, setFourScore] = useState(localStorage.getItem('fourScore'))
  const [fiveScore, setFiveScore] = useState(localStorage.getItem('fiveScore'))
  const [sixScore, setSixScore] = useState(localStorage.getItem('sixScore'))
  const [sevenScore, setSevenScore] = useState(
    localStorage.getItem('sevenScore')
  )
  const [eightScore, setEightScore] = useState(
    localStorage.getItem('eightScore')
  )
  const [nineScore, setNineScore] = useState(localStorage.getItem('nineScore'))
  const [tenScore, setTenScore] = useState(localStorage.getItem('tenScore'))

  // const increaseZeroScore = () => {
  //   setZeroScore((prevScore) => {
  //     const newScore = Number(prevScore) + 1
  //     localStorage.setItem('zeroScore', newScore)
  //     return newScore
  //   })
  // }

  const increaseOneScore = () => {
    setOneScore((prevScore) => {
      const newScore = Number(prevScore) + 1
      localStorage.setItem('oneScore', newScore)
      return newScore
    })
  }

  const increaseTwoScore = () => {
    setTwoScore((prevScore) => {
      const newScore = Number(prevScore) + 1
      localStorage.setItem('twoScore', newScore)
      return newScore
    })
  }

  const increaseThreeScore = () => {
    setThreeScore((prevScore) => {
      const newScore = Number(prevScore) + 1
