      // })
    } else {
      socket.off('new_data')
    }
  }, [connection])

  // useEffect(() => {
  //   if (
  //     position1 === testLog.position1 &&
  //     position2 === testLog.position2 &&
  //     position3 === testLog.position3 &&
  //     currentDanceMove === testLog.danceMove
