  // console.log(testLog.position3)
  // console.log(currentDanceMove)
  // console.log(testLog.danceMove)
  // console.log(correctness)

  return (
    <>
      <Grid container justify='center'>
        <Grid item>
          <DancerCard
            name='Paula'
            position={position[0]}
            userImage='6CgkUjUl4og'
            danceMove={danceMove}
          />
        </Grid>

        <Grid item>
          <DancerCard
            name='Robinson'
            position={position[1]}
            userImage='sibVwORYqs0'
            danceMove={danceMove}
          />
        </Grid>

        <Grid item>
          <DancerCard
            name='Erik'
            position={position[2]}
            userImage='d2MSDujJl2g'
            danceMove={danceMove}
          />
        </Grid>

        {/* <AnimateSharedLayout>
          <motion.div
            drag
            dragElastic={0.2}
            dragMomentum={false}
            layout
