import React from 'react'
import Carousel from 'react-elastic-carousel'
import Item from './Item'
import './VideoCarousel.css'
import ReactPlayer from 'react-player'
import { useState, useEffect } from 'react'
// import io from 'socket.io-client'
// const socket = io('http://localhost:3000', {
//   transports: ['websocket', 'polling'],
// })

const breakPoints = [
  { width: 1, itemsToShow: 1 },
  { width: 550, itemsToShow: 2 },
  { width: 768, itemsToShow: 3 },
  { width: 1200, itemsToShow: 4 },
]

// function handleOnPlayEvent(danceMove) {
//   switch (danceMove) {
//     case 'dab':
//       // code block
//       io.emit('video_count')

//       break
//     case 'elbowkick':
//       // code block
//       break
//     case 'gun':
//       // code block
//       break
//     case 'hair':
//       // code block
//       break
//     case 'listen':
//       // code block
//       break
//     case 'pointhigh':
//       // code block
//       break
//     case 'sidepump':
//       // code block
//       break
//     case 'wipetable':
//       // code block
//       break

//     default:
//     // code block
//   }
// }

function VideoCarousel() {
  const [count, setCount] = useState(0)

  const increase = () => {
    setCount((prevCount) => {
      const newCount = Number(prevCount) + 1
      localStorage.setItem('count', newCount)
      return newCount
    })
  }

  useEffect(() => {
    const initialValue = localStorage.getItem('count')
    if (initialValue) setCount(initialValue)
  }, [])

  // Just to show you the localStorage Value
  console.log(localStorage.getItem('count'))

  return (
    <>
      <div className='videocarousel'>
        <Carousel breakPoints={breakPoints}>
          <Item>
            <ReactPlayer
              url='https://www.youtube.com/embed/19GptZzhhjM'
              onEnded={increase}
            />{' '}
          </Item>
          <Item>
            <ReactPlayer url='https://www.youtube.com/embed/hPFdDhZzwKM' />{' '}
          </Item>
          <Item>
            <ReactPlayer url='https://www.youtube.com/embed/l1ApsUn-6Pw' />{' '}
          </Item>
          <Item>
            <ReactPlayer url='https://www.youtube.com/embed/0Mp4zz-33Ow' />{' '}
          </Item>

          <Item>
            <ReactPlayer url='https://www.youtube.com/embed/Kx3pUsL0hyo' />{' '}
          </Item>
          <Item>
            <ReactPlayer url='https://www.youtube.com/embed/WMoRZ0K7qFw' />{' '}
          </Item>
          <Item>
            <ReactPlayer url='https://www.youtube.com/embed/iTQs6-rYasY' />{' '}
          </Item>
        </Carousel>
      </div>
    </>
  )
}

export default VideoCarousel
