import React from 'react'
import { Card, Typography, Grid } from '@material-ui/core'
import { makeStyles } from '@material-ui/core/styles'
import PieChart from '../components/PieChart/PieChart'
import OutlinedCard from '../components/OutlinedCard/OutlinedCard'
import LineChart from '../components/LineChart/LineChart'
import BarChartScore from '../components/BarChart/BarChartScore'
import { useState, useEffect } from 'react'
const useStyles = makeStyles({
  root: {
    display: 'flex',
    justifyContent: 'space-around',
    marginTop: 120,
  },
  heading: {
    fontSize: '4.5em',
    letterSpacing: '-1px',
    backgroundColor: '#503e9d',
    color: 'white',
  },
})

function Progress() {
  const [zeroCount, setZeroCount] = useState(0)
  const [oneCount, setOneCount] = useState(0)
  const [twoCount, setTwoCount] = useState(0)
  const [threeCount, setThreeCount] = useState(0)
  const [fourCount, setFourCount] = useState(0)
  const [fiveCount, setFiveCount] = useState(0)
  const [sixCount, setSixCount] = useState(0)
  const [sevenCount, setSevenCount] = useState(0)
  const [eightCount, setEightCount] = useState(0)
  const [nineCount, setNineCount] = useState(0)
  const [tenCount, setTenCount] = useState(0)

  const classes = useStyles()

  useEffect(() => {
    let savedScore = localStorage.getItem('savedScore')
    if (savedScore === 0) {
      setZeroCount((prevCount) => prevCount + 1)
    } else if (savedScore === 1) {
      setOneCount((prevCount) => prevCount + 1)
    } else if (savedScore === 2) {
      setTwoCount((prevCount) => prevCount + 1)
    } else if (savedScore === 3) {
      setThreeCount((prevCount) => prevCount + 1)
    } else if (savedScore === 4) {
      setFourCount((prevCount) => prevCount + 1)
    } else if (savedScore === 5) {
      setFiveCount((prevCount) => prevCount + 1)
    } else if (savedScore === 6) {
      setSixCount((prevCount) => prevCount + 1)
    } else if (savedScore === 7) {
      setSevenCount((prevCount) => prevCount + 1)
    } else if (savedScore === 8) {
      setEightCount((prevCount) => prevCount + 1)
    } else if (savedScore === 9) {
      setNineCount((prevCount) => prevCount + 1)
    } else if (savedScore === 10) {
      setTenCount((prevCount) => prevCount + 1)
    }
  }, [savedScore])

  return (
    <>
      <Grid container justify='center' style={{ marginTop: '-10px' }}>
        <Grid item xs={12}>
          <Typography variant='h3' align='center' className={classes.heading}>
            {' '}
            Progress
          </Typography>
        </Grid>
      </Grid>
      <Grid container direction='column'>
        <Grid container item>
          <Grid item>
            <BarChartScore
              zeroCount={zeroCount}
              oneCount={oneCount}
              twoCount={twoCount}
              threeCount={threeCount}
              fourCount={fourCount}
              fiveCount={fiveCount}
              sixCount={sixCount}
              sevenCount={sevenCount}
              eightCount={eightCount}
              nineCount={nineCount}
              tenCount={tenCount}
            />
            {/* <PieChart /> */}
          </Grid>

          <Grid item className={classes.root}>
            {/* <OutlinedCard title='Total Games' count='12' />
            <OutlinedCard title='Total Dance Moves' count='111' /> */}
          </Grid>
        </Grid>
        <Grid item>{/* <LineChart /> */}</Grid>
      </Grid>
    </>
  )
}

export default Progress
