import React, { useRef, useEffect, useCallback } from 'react'
import { useSpring, animated } from 'react-spring'
import styled from 'styled-components'
import { MdClose } from 'react-icons/md'
import { Link } from 'react-router-dom'
import { useState } from 'react'

const Background = styled.div`
  width: 100%;
  height: 100%;
  background: rgba(0, 0, 0, 0.8);
  position: fixed;
  display: flex;
  justify-content: center;
  align-items: center;
`

const ModalWrapper = styled.div`
  width: 800px;
  height: 500px;
  box-shadow: 0 5px 16px rgba(0, 0, 0, 0.2);
  background: #fff;
  color: #000;
  display: grid;
  grid-template-columns: 1fr 1fr;
  position: relative;
  z-index: 10;
  border-radius: 10px;
`

const ModalImg = styled.img`
  width: 100%;
  height: 100%;
  border-radius: 10px 0 0 10px;
  background: #000;
`

const ModalContentLeft = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: flexstart;
  align-items: center;
  line-height: 1.8;
  color: #141414;
  p {
    margin-bottom: 1rem;
  }
  button {
    padding: 10px 24px;
    background: #141414;
    color: #fff;
    border: none;
  }
`
const ModalContentRight = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  line-height: 1.8;
  color: #141414;
  p {
    margin-bottom: 1rem;
  }
  button {
    padding: 10px 24px;
    background: #141414;
    color: #fff;
    border: none;
  }
`

const CloseModalButton = styled(MdClose)`
  cursor: pointer;
  position: absolute;
  top: 20px;
  right: 20px;
  width: 32px;
  height: 32px;
  padding: 0;
  z-index: 10;
`

export const Modal = ({ showModal, setShowModal, score, setScore }) => {
  const modalRef = useRef()

  const animation = useSpring({
    config: {
      duration: 250,
    },
    opacity: showModal ? 1 : 0,
    transform: showModal ? `translateY(0%)` : `translateY(-100%)`,
  })

  const closeModal = (e) => {
    if (modalRef.current === e.target) {
      setShowModal(false)
      setScore(0)
    }
  }

  const closeModalWithCross = () => {
    setShowModal(false)
    setScore(0)
  }

  const [zeroCount, setZeroCount] = useState(0)
  const [oneCount, setOneCount] = useState(0)
  const [twoCount, setTwoCount] = useState(0)
  const [threeCount, setThreeCount] = useState(0)
  const [fourCount, setFourCount] = useState(0)
  const [fiveCount, setFiveCount] = useState(0)
  const [sixCount, setSixCount] = useState(0)
  const [sevenCount, setSevenCount] = useState(0)
  const [eightCount, setEightCount] = useState(0)
  const [nineCount, setNineCount] = useState(0)
  const [tenCount, setTenCount] = useState(0)

  const increaseZeroCount = () => {
    setZeroCount((prevCount) => {
      const newCount = Number(prevCount) + 1
      localStorage.setItem('zeroScore', newCount)
      return newCount
    })
  }

  const increaseOneCount = () => {
    setOneCount((prevCount) => {
      const newCount = Number(prevCount) + 1
      localStorage.setItem('oneScore', newCount)
      return newCount
    })
  }

  const increaseTwoCount = () => {
    setTwoCount((prevCount) => {
      const newCount = Number(prevCount) + 1
      localStorage.setItem('twoScore', newCount)
      return newCount
    })
  }

  const increaseThreeCount = () => {
    setThreeCount((prevCount) => {
      const newCount = Number(prevCount) + 1
      localStorage.setItem('threeScore', newCount)
      return newCount
    })
  }

  const handleSaveButton = () => {
    if (score === 0) {
      increaseZeroCount()
    } else if (score === 1) {
      increaseOneCount()
    } else if (score === 2) {
      increaseTwoCount()
    } else if (score === 3) {
      increaseThreeCount()
    }
  }

  const keyPress = useCallback(
    (e) => {
      if (e.key === 'Escape' && showModal) {
        setShowModal(false)
        setScore(0)
        console.log('I pressed')
      }
    },
    [setShowModal, showModal]
  )

  useEffect(() => {
    document.addEventListener('keydown', keyPress)
    return () => document.removeEventListener('keydown', keyPress)
  }, [keyPress])

  return (
    <>
      {showModal ? (
        <Background onClick={closeModal} ref={modalRef}>
          <animated.div style={animation}>
            <ModalWrapper showModal={showModal}>
              {/* <ModalImg
                src={require('../../images/Claudia.png')}
                alt='camera'
              /> */}
              <ModalContentLeft style={{ justifyContent: 'flexstart' }}>
                <h1>Score</h1>
                <p>{score}/ 10</p>

                <h1>Overall Sync</h1>
                <p>85%</p>

                <h1>Overall Accuracy</h1>
                <p>80%</p>

                <button onClick={handleSaveButton}>Save</button>
              </ModalContentLeft>
              <ModalContentRight>
                {score > 7 ? (
                  <h1>You are doing great! Keep it up!</h1>
                ) : score > 5 ? (
                  <h1>Practice more! You can be better</h1>
                ) : (
                  <h1>Do not give up! You can do it!</h1>
                )}

                <p>Tips: Watch video tutorial to improve further!</p>
                <Link to='/dancebase'>
                  <button>Watch Now</button>
                </Link>
              </ModalContentRight>
              <CloseModalButton
                aria-label='Close modal'
                // onClick={() => setShowModal((prev) => !prev)}
                onClick={closeModalWithCross}
              />
            </ModalWrapper>
          </animated.div>
        </Background>
      ) : null}
    </>
  )
}
