import React from 'react'
import { Grid, Typography, Button } from '@material-ui/core'
import InputAdornments from '../components/InputAdornments/InputAdornments'
import { makeStyles } from '@material-ui/core/styles'
const useStyles = makeStyles((theme) => ({
  heading: {
    fontSize: '4.5em',
    letterSpacing: '-1px',
    backgroundColor: '#503e9d',
    color: 'white',
  },
}))
function Authenticate(props) {
  const { access } = props
  const classes = useStyles()

  const ReactCSSTG = React.addons.CSSTransitionGroup

  // Main app
  class App extends React.Component {
    constructor(props) {
      super(props)
      this.state = {
        isVisible: true,
      }
      // Bindings
      this.handleSubmit = this.handleSubmit.bind(this)
      this.handleRemount = this.handleRemount.bind(this)
    }

    handleSubmit(e) {
      e.preventDefault()
      this.setState(
        {
          isVisible: false,
        },
        function () {
          console.log(this.state.isVisible)
        }
      )
      return false
    }
    handleRemount(e) {
      this.setState(
        {
          isVisible: true,
        },
        function () {
          console.log(this.state.isVisible)
        }
      )
      e.preventDefault()
    }
    render() {
      // const for React CSS transition declaration
      let component = this.state.isVisible ? (
        <Modal onSubmit={this.handleSubmit} key='modal' />
      ) : (
        <ModalBack onClick={this.handleRemount} key='bringitback' />
      )

      return (
        <ReactCSSTG
          transitionName='animation'
          transitionAppear={true}
          transitionAppearTimeout={500}
          transitionEnterTimeout={500}
          transitionLeaveTimeout={300}
        >
          {component}
        </ReactCSSTG>
      )
    }
  }

  // Modal
  class Modal extends React.Component {
    render() {
      return (
        <div className='Modal'>
          <Logo />
          <form onSubmit={this.props.onSubmit}>
            <Input type='text' name='username' placeholder='username' />
            <Input type='password' name='password' placeholder='password' />
            <button> Sign In</button>
          </form>
          <div className='social-signin'>
            <button className='fb' onClick={this.props.onClick}>
              <i className='fa fa-facebook' aria-hidden='true'></i>
            </button>
            <button className='tw' onClick={this.props.onClick}>
              <i className='fa fa-twitter' aria-hidden='true'></i>
            </button>
          </div>
          <a href='#'>Lost your password ?</a>
        </div>
      )
    }
  }

  // Generic input field
  class Input extends React.Component {
    render() {
      return (
        <div className='Input'>
          <input
            type={this.props.type}
            name={this.props.name}
            placeholder={this.props.placeholder}
            required
            autocomplete='false'
          />
          <label for={this.props.name}></label>
        </div>
      )
    }
  }

  // Fake logo
  class Logo extends React.Component {
    render() {
      return (
        <div className='logo'>
          <i className='fa fa-bug' aria-hidden='true'></i>
          <span> awesome logo </span>
        </div>
      )
    }
  }

  // Button to brind the modal back
  class ModalBack extends React.Component {
    render() {
      return (
        <button
          className='bringitback'
          onClick={this.props.onClick}
          key={this.props.className}
        >
          Brind the modal back !
        </button>
      )
    }
  }

  return (
    <>
      {/* <Grid container justify='center'>
        <Grid item xs={12}>
          <Typography variant='h3' align='center' className={classes.heading}>
            {' '}
            Developer Mode
          </Typography>
        </Grid>
      </Grid>
      <Grid container justify='center' style={{ marginTop: '25vh' }}>
        <Grid item xs={12}>
          <Typography variant='h4' align='center'>
            In order to access Developer Mode, please enter the developer
            password.
          </Typography>

          <InputAdornments access={access} />
        </Grid>
      </Grid> */}
    </>
  )
}

export default Authenticate
