    return () => document.removeEventListener('keydown', keyPress)
  }, [keyPress])

  const handleSaveButton = () => {
    if (flag) {
      if (score === 1) {
        increaseOneScore()
      } else if (score === 2) {
        increaseTwoScore()
      } else if (score === 3) {
        increaseThreeScore()
      } else if (score === 4) {
        increaseFourScore()
      } else if (score === 5) {
        increaseFiveScore()
      } else if (score === 6) {
        increaseSixScore()
      } else if (score === 7) {
        increaseSevenScore()
      } else if (score === 8) {
        increaseEightScore()
      } else if (score === 9) {
        increaseNineScore()
      } else if (score === 10) {
        increaseTenScore()
      }
    }

    setAccuracyDatas((oldDatas) => [...oldDatas, Math.floor(accuracyAvg)])
    localStorage.setItem('accuracyDatas', JSON.stringify(accuracyDatas))

    setSyncDatas((oldDatas) => [...oldDatas, Math.floor(syncAvg)])
    localStorage.setItem('syncDatas', JSON.stringify(syncDatas))

    setTime((oldDatas) => [...oldDatas, new Date().toLocaleTimeString()])
    localStorage.setItem('time', JSON.stringify(time))

    setFlag(false)
  }

  return (
    <>
      {showModal ? (
        <Background onClick={closeModal} ref={modalRef}>
          <animated.div style={animation}>
            <ModalWrapper showModal={showModal}>
              <ModalContentLeft style={{ justifyContent: 'center' }}>
                <Grid container direction='column'>
                  <Grid item xs={12}>
                    <h1>Score</h1>
                    <p>{score}/ 10</p>
                  </Grid>

                  <Grid item xs={12}>
                    <h1>Overall Sync</h1>
                    <p>{Math.floor(syncAvg)}%</p>
                  </Grid>

                  <Grid item xs={12}>
                    <h1>Overall Accuracy</h1>
                    <p>{Math.floor(accuracyAvg)}%</p>
                  </Grid>
                </Grid>

                <button onClick={handleSaveButton}>Save</button>
              </ModalContentLeft>
              <ModalContentRight>
                {score > 7 ? (
                  <h1>You are doing great! Keep it up!</h1>
                ) : score > 5 ? (
                  <h1>Practice more! You can be better</h1>
                ) : (
                  <h1>Do not give up! You can do it!</h1>
                )}

                <p>Tips: Watch video tutorial to improve further!</p>
                <Link to='/dancebase'>
                  <button>Watch Now</button>
                </Link>
              </ModalContentRight>
              <CloseModalButton
                aria-label='Close modal'
                onClick={closeModalWithCross}
              />
            </ModalWrapper>
          </animated.div>
        </Background>
      ) : null}
    </>
  )
}
